	i = i + 1;
	p.push({
		q: n_temp[1],
		w: n1.lastIndex
	});
}
i_tmp = i;n_tmp='';

while ((n_temp = n3.exec(result)) != null) {
	//Ëé∑ÂèñÊØèËØùÁöÑÊ†áÈ¢ò
	n_sub = n_temp[1];
	//Âà§Êñ≠VIP
