		n_sub = n_sub.match(/span>(.+)<\/span><spa/)[1];
		n_sub = 'üîí' + n_sub;
	}
	//Ê∑ªÂä†Âç∑Ê†áÈ¢òÂíå‰∫åÁ∫ßÊ†áÈ¢ò
	if (p[0] != null) {
		i = i_tmp;
		p_tmp = p[i].w;
		while (p_tmp > n3.lastIndex) {
			i = i - 1;
			p_tmp = p[i].w;
		}
		n_pre = p[i].q;
if(n_pre!=n_tmp){
   list.push({
		  k: '‚ô¶'+n_pre+'‚ô¶',
		  v: 'http://localhost/'+n_pre
	});n_tmp=n_pre;
}
		n_pre ='';
	}
	//‰øùÂ≠òÊï∞ÊçÆÂà∞Êï∞ÁªÑ


	n = n_pre + n_sub;
	t = t + 1;
