@js:

function getTranslation() {
	importClass(org.jsoup.Jsoup);
	let doc = Jsoup.parse(result).select('.forum-content p'),
	trans = (/trans='(\d+)/).test(result)?result.match(/trans='(\d+)/)[1]:'';

	//尝试获取翻译后的段落
	let TransParts = (function() {
		if(doc.toArray().length<=trans&&doc.toArray().length>1){
			let body = "plxf=getTranslation&plxa[]=" + trans;
			let host = "https://www.esjzone.cc";
			let url = (/esjzone\.cc/).test(baseUrl) ? baseUrl: (host + baseUrl);
			let options = {
				method: "post",
				body: body
			};
			url = url + "," + JSON.stringify(options);
			//url=url+'@'+body;
			let responseStr = String(java.ajax(url));
			if ((/JinJing/).test(responseStr)) {
				return responseStr.replace(/<.?JinJing>/g, '');
			}else return doc.html()
		}else return doc.html()
	})();

	let Html = TransParts;
	let hint = "无法显示内容，书籍可能下架或为其他站内容，请到原网页查看\n\n章节链接：\n" + baseUrl;
	let adstr = "(adsbygoogle = window.adsbygoogle || []).push({});";
	return (Html == null || Html == undefined || Html == '') ? hint: Html.replace(adstr, '');

}
result = (/\.google\.cn/).test(baseUrl) ? decodeURIComponent(baseUrl.replace(/^.+[&?]text=/, '')) : getTranslation();

